//=========--------==========INTERNAL FUNCTIONS=========--------==========
//Generate and return a last name that concatenates two random words
function FN_JoinNames()
{
   var firstIndex = FN_randomRanged(0, ar_firstParts.length-1);
   var secondIndex = FN_randomRanged(0, ar_lastParts.length-1);
   return ar_firstParts[firstIndex] + ar_lastParts[secondIndex];
}
//Generate a random number between lowerInput and upperInput
//remember that array indexes start at 0
export const FN_randomRanged = (min, max) => 
{
   var result = Math.floor(Math.random() * (max - min + 1) + min);
   // console.log("\tRandom number: "+ result +". \n\tMin was: "+ min + ". \n\tMax was: "+max);
   return result;
}
 
//function to generate a random character (consonants or vowels, selected with toggle parameter) and return it
export const randomCharacter = (toggle) =>
{
   if (toggle === 1)
      {
        var consonant = ar_Consonants[FN_randomRanged(0, (ar_Consonants.length-1))];
        //    index = Math.floor(Math.random() * (ar_AllMaleNameArrays[raceSelected].length - 1 + 1) + 1);
        return consonant
      }
   else if (toggle === 0)
      {
         var vowel = ar_Vowels[FN_randomRanged(0, (ar_Vowels.length-1))];
        return vowel;
      }
   else
      {
        console.log("\tIn randomCharacter function: Parameter 'toggle' was not 0 or 1")     
      }
}

//function to randomly choose one of the two genders
export const FN_get_random_gender = () =>
{
   let result = "[None]";
   var index = FN_randomRanged(0, 1);
   result = ar_AllGenders[index];
   return result;
}

export const FN_get_random_race = () =>
{
   let result = "[None]";
   let raceIndex = FN_randomRanged(0, (ar_races.length-1));
   result = ar_races[raceIndex];
   return result;
}

// EXPORT function to generate a random last name from a bunch of random letters
export const FN_randLastName = () =>
{
   var selection = FN_randomRanged(1,3);

      //short last name, four characters (Consonant, Vowel, Consonant, Consonant)
   if(selection === 1)
   {
      //console.log("Selection was 1. Short last name!");
      //eg - Forx
      var short_firstCha = String(randomCharacter(1));
      var short_secondCha = String(randomCharacter(0));
      var short_thirdCha = String(randomCharacter(1));
      var short_fourthCha = String(randomCharacter(1));
      //some last names can be weird. this loop generates new characters if some are weird
      for(var fourth_correct = false; fourth_correct === false;)
      {
         if(short_fourthCha === "k" || short_fourthCha === "q" || short_fourthCha === "z" || short_fourthCha === "v" || short_fourthCha === "w" || short_fourthCha === "x" || short_fourthCha === "l")
         {
            short_fourthCha = String(randomCharacter(1))
         }
         else
         {
            fourth_correct = true;
         }

      }
      
      var short_product = short_firstCha.toUpperCase() + short_secondCha + short_thirdCha + short_fourthCha;
      return short_product;
   }

   else if (selection === 2)
   {
      //console.log("Selection was 2. Medium last name!");
      //eg - Millet
      var med_firstCha = String(randomCharacter(1));
      var med_secondCha = String(randomCharacter(0));
      var med_thirdCha = String(randomCharacter(1));
      var med_fourthCha = String(randomCharacter(1));
      var med_fifthCha = String(randomCharacter(0));
      var med_sixthCha = String(randomCharacter(1));

      //some last names can be weird. this loop generates new characters if some are weird
      for(var third_correct = false; third_correct === false;)
      {
         if(med_thirdCha === "k" || med_thirdCha === "q" || med_thirdCha === "z" || med_thirdCha === "v" || med_thirdCha === "w" || med_thirdCha === "x"|| med_thirdCha === "l")
         {
            med_thirdCha = String(randomCharacter(1))
            //console.log("On med last name, third char was: "+ last +". Set it as: "+med_thirdCha);
         }
         else
         {
            third_correct = true;
         }

      }

      for(var fourthMed_correct = false; fourthMed_correct === false;)
      {
         if(med_fourthCha === "k" || med_fourthCha === "q" || med_fourthCha === "z" || med_fourthCha === "v" || med_fourthCha === "w" || med_fourthCha === "x"|| med_fourthCha === "l")
         {
            med_fourthCha = String(randomCharacter(1))
            //console.log("On med last name, third char was: "+ lastEntry +". Set it as: "+med_fourthCha);
         }
         else
         {
            fourthMed_correct = true;
         }

      }

      var med_product = med_firstCha.toUpperCase() + med_secondCha + med_thirdCha + med_fourthCha + med_fifthCha + med_sixthCha;
      return med_product;
   }

   else if (selection === 3)
   {
      //console.log("Selection was 3. SUPER last name!");
   
      var superProduct = FN_JoinNames()
      return superProduct;
   }   

   else{
      console.log("When generating a last name, the selection was '"+ selection + "'. NOT ALLOWED!")
   }
    
}

//function to generate an npc name, based on sex and race
export const FN_get_npc_name = (genderSelected, raceSelected) =>
     {
      var result = null;

      //if the gender is 0 (or male), then pick a random name from the associated race list
      if(genderSelected === 0)
      {
         var male_name_index = FN_randomRanged(0, (ar_AllMaleNameArrays[raceSelected].length-1));
         result = ar_AllMaleNameArrays[raceSelected][male_name_index];  
      }
      //if the gender is 1 (or female), then pick a random name from the associated race list
      else if (genderSelected === 1)
      {
         var female_name_index = FN_randomRanged(0, ar_AllFemaleNameArrays[raceSelected].length-1);
         result = ar_AllFemaleNameArrays[raceSelected][female_name_index];
      }

      return result;
     }